var CentinelAuthenticate=Class.create();CentinelAuthenticate.prototype={initialize:function(a,b){this._isAuthenticationStarted=false;this._relatedBlocks=new Array();this.centinelBlockId=a;this.iframeId=b;if(this._isCentinelBlocksLoaded()){$(this.centinelBlockId).hide()}},isAuthenticationStarted:function(){return this._isAuthenticationStarted},addRelatedBlock:function(a){this._relatedBlocks[this._relatedBlocks.size()]=a},_hideRelatedBlocks:function(){for(var a=0;a<this._relatedBlocks.size();a++){$(this._relatedBlocks[a]).hide()}},_showRelatedBlocks:function(){for(var a=0;a<this._relatedBlocks.size();a++){$(this._relatedBlocks[a]).show()}},_isRelatedBlocksLoaded:function(){for(var a=0;a<this._relatedBlocks.size();a++){if(!$(this._relatedBlocks[a])){return false}}return true},_isCentinelBlocksLoaded:function(){if(!$(this.centinelBlockId)||!$(this.iframeId)){return false}return true},start:function(a){if(this._isRelatedBlocksLoaded()&&this._isCentinelBlocksLoaded()){this._hideRelatedBlocks();$(this.iframeId).src=a;$(this.centinelBlockId).show();this._isAuthenticationStarted=true}},success:function(){if(this._isRelatedBlocksLoaded()&&this._isCentinelBlocksLoaded()){this._showRelatedBlocks();$(this.centinelBlockId).hide();this._isAuthenticationStarted=false}},cancel:function(){if(this._isAuthenticationStarted){if(this._isRelatedBlocksLoaded()){this._showRelatedBlocks()}if(this._isCentinelBlocksLoaded()){$(this.centinelBlockId).hide();$(this.iframeId).src=""}this._isAuthenticationStarted=false}}};